import { useState } from "react";
import "./House.css";

const ITEMS_PER_PAGE = 20;

const PROJECTS = [
  { id: "1", projectName: "Green Villa", projectType: "villa" },
  { id: "2", projectName: "Sky Heights", projectType: "apartment" }
];

export default function Houses() {
  const [projects] = useState(PROJECTS);
  const [selectedId, setSelectedId] = useState("");
  const [houses, setHouses] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const handleChange = (id) => {
    setSelectedId(id);
    setCurrentPage(1);
    setHouses([]);
  };

  const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
  const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
  const currentHouses = houses.slice(indexOfFirstItem, indexOfLastItem);

  const handleAddBooking = () => {
    alert(`Add booking for project ID: ${selectedId}`);
    // later: navigate("/add-booking") or open modal
  };

  return (
    <div className="house-container">
      <h1>Select Project</h1>

      <select
        className="project-select"
        value={selectedId}
        onChange={(e) => handleChange(e.target.value)}
      >
        <option value="">Select Project</option>
        {projects.map((p) => (
          <option key={p.id} value={p.id}>
            {p.projectName} ({p.projectType})
          </option>
        ))}
      </select>

      {/* âœ… Add Booking Button */}
      {selectedId && (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <button
            className="bookings-btn"
            onClick={handleAddBooking}
          >
            Add Booking
          </button>
        </div>
      )}

      {/* No houses data */}
      {selectedId && currentHouses.length === 0 && (
        <p className="no-data">No houses found</p>
      )}
    </div>
  );
}
