import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

export default function Bookings() {
  const today = new Date().toISOString().split("T")[0];

  /* ================= STATES ================= */
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false); // ✅ modal control
  const [payAmount, setPayAmount] = useState("");

  const [form, setForm] = useState({
    projectId: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: 0,
    advancePayment: 0,
    pendingAmount: 0,
    emiMonths: 0,
    monthlyEmi: 0,
    bookingDate: today,
  });

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadProjects();
    loadBookings();
  }, []);

  const loadProjects = async () => {
    const res = await api.get("/lily");
    setProjects(res.data.data);
  };

  const loadBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  /* ================= PROJECT MAP ================= */
  const projectMap = {};
  projects.forEach(p => (projectMap[p.id] = p.projectName));

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    setForm(prev => ({
      ...prev,
      projectId: pid,
      houseNumber: "",
      totalPayment: 0,
      advancePayment: 0,
      pendingAmount: 0,
      emiMonths: 0,
      monthlyEmi: 0,
    }));

    try {
      const res = await api.get(`/lily/houses/${pid}`);
      setHouses(res.data.data.filter(h => h.status === "available"));
    } catch {
      setHouses([]);
    }
  };

  /* ================= HOUSE CHANGE ================= */
  const handleHouseChange = (houseNo) => {
    const house = houses.find(h => h.houseNumber === houseNo);
    const price = house?.price || 0;

    setForm(prev => ({
      ...prev,
      houseNumber: houseNo,
      totalPayment: price,
      pendingAmount: price - Number(prev.advancePayment || 0),
    }));
  };

  /* ================= SUBMIT BOOKING ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        projectId: Number(form.projectId),
        houseNumber: form.houseNumber,
        customerName: form.customerName,
        mobileNo: form.mobileNo,
        paymentType: form.paymentType,
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment || 0),
        pendingAmount: Number(form.pendingAmount),
        bookingDate: form.bookingDate,
        emiMonths: form.paymentType === "emi" ? Number(form.emiMonths) : 0,
        monthlyEmi: form.paymentType === "emi" ? Number(form.monthlyEmi) : 0,
      });

      alert("✅ Booking Created Successfully");

      setShowForm(false);
      setForm({
        projectId: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: 0,
        advancePayment: 0,
        pendingAmount: 0,
        emiMonths: 0,
        monthlyEmi: 0,
        bookingDate: today,
      });

      setHouses([]);
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ Booking Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div className="booking-container">

      {/* ✅ BUTTON */}
      <div style={{ textAlign: "right", marginBottom: 20 }}>
        <button className="primary-btn" onClick={() => setShowForm(true)}>
          ➕ Create Booking
        </button>
      </div>

      {/* ================= MODAL FORM ================= */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-card">
            <h2>Create Booking</h2>

            <form onSubmit={submitBooking} className="booking-form">

              <select required value={form.projectId}
  onChange={e => handleProjectChange(e.target.value)}>
  <option value="">Select Project</option>
  {projects.map(p => (
    <option key={p.id} value={p.id}>{p.projectName}</option>
  ))}
</select>


              <select required value={form.houseNumber}
  onChange={e => handleHouseChange(e.target.value)}>
  <option value="">Select House</option>
  {houses.map(h => (
    <option key={h.houseNumber} value={h.houseNumber}>
      House No {h.houseNumber}
    </option>
  ))}
</select>
<input
  type="text"
  placeholder="Customer Full Name"
  required
  value={form.customerName}
  onChange={e => setForm({ ...form, customerName: e.target.value })}
/>

<input
  type="tel"
  placeholder="Mobile Number"
  required
  maxLength={10}
  value={form.mobileNo}
  onChange={e => setForm({ ...form, mobileNo: e.target.value })}
/>

              {/* ✅ DATE PICKER */}
              <input
                type="date"
                value={form.bookingDate}
                onChange={e => setForm({ ...form, bookingDate: e.target.value })}
              />

              <select value={form.paymentType}
  onChange={e => setForm({ ...form, paymentType: e.target.value })}>
  <option value="cash">Cash </option>
  <option value="emi">EMI Payment</option>
</select>

              <input
  type="number"
  readOnly
  placeholder="Total Amount"
  value={form.totalPayment}
/>

              <input
  type="number"
  placeholder="Advance Payment"
  required
  value={form.advancePayment}
  onChange={e => {
    const adv = Number(e.target.value);
    setForm(prev => ({
      ...prev,
      advancePayment: adv,
      pendingAmount: Number(prev.totalPayment) - adv,
    }));
  }}
/>

             <input
  type="number"
  readOnly
  placeholder="Pending Amount"
  value={form.pendingAmount}
/>

              {form.paymentType === "emi" && (
                <>
                  <input type="number" placeholder="EMI Months"
                    value={form.emiMonths}
                    onChange={e => setForm({ ...form, emiMonths: e.target.value })}
                  />
                  <input type="number" placeholder="Monthly EMI"
                    value={form.monthlyEmi}
                    onChange={e => setForm({ ...form, monthlyEmi: e.target.value })}
                  />
                </>
              )}

              <div className="modal-actions">
                <button type="submit" disabled={loading}>
                  {loading ? "Booking..." : "Save"}
                </button>
                <button type="button" className="secondary"
                  onClick={() => setShowForm(false)}>
                  Cancel
                </button>
              </div>

            </form>
          </div>
        </div>
      )}

      {/* ================= BOOKING LIST ================= */}
      <h2 className="page-title">Booking List</h2>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Project</th>
            <th>House</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Total</th>
            <th>Advance</th>
            <th>Pending</th>
            <th>Date</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b => (
            <tr key={b._id}>
              <td>{b.bookingId}</td>
              <td>{projectMap[b.projectId]}</td>
              <td>{b.houseNumber}</td>
              <td>{b.customerName}</td>
              <td>{b.paymentType.toUpperCase()}</td>
              <td>₹{b.totalPayment}</td>
              <td>₹{b.advancePayment}</td>
              <td>₹{b.pendingAmount}</td>
              <td>{new Date(b.bookingDate).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

    </div>
  );
}
