import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

export default function Bookings() {
  const [bookings, setBookings] = useState([]);
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);

  const [showForm, setShowForm] = useState(false);

  const [form, setForm] = useState({
    project: "",
    projectId: "",
    projectName: "",
    projectType: "",
    perHouseCost: "",
    house: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
    emiMonths: "",
    emiStartDate: ""
  });

  /* ================= LOAD BOOKINGS ================= */
  useEffect(() => {
    fetchBookings();
    fetchProjects();
  }, []);

  const fetchBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  /* ================= LOAD PROJECTS ================= */
  const fetchProjects = async () => {
    const res = await api.get("/lily"); // your project API
    setProjects(res.data.data);
  };

  /* ================= LOAD HOUSES (PROJECT WISE) ================= */
  const fetchHouses = async (projectId) => {
  try {
    const res = await api.get(`/lily/houses/${projectId}`);
    setHouses(res.data.data.filter(h => h.status === "available"));
  } catch (err) {
    alert(err.response?.data?.message || "Failed to load houses");
    setHouses([]);
  }
};

  /* ================= HANDLE PROJECT CHANGE ================= */
  const handleProjectChange = (projectMongoId) => {
  const project = projects.find(p => p._id === projectMongoId);

  setForm({
    ...form,
    project: project._id,        // Mongo ID
    projectId: project.projectId, // NUMBER
    projectName: project.projectName,
    projectType: project.projectType,
    perHouseCost: project.perHouseCost,
    house: "",
    houseNumber: ""
  });

  // ðŸ”¥ VERY IMPORTANT
  fetchHouses(project.projectId); // NUMBER
};



  /* ================= HANDLE HOUSE CHANGE ================= */
  const handleHouseChange = (houseId) => {
    const house = houses.find(h => h._id === houseId);
    setForm({
      ...form,
      house: house._id,
      houseNumber: house.houseNumber
    });
  };

  /* ================= SUBMIT BOOKING ================= */
  const submitBooking = async (e) => {
    e.preventDefault();

    await api.post("/bookings/create", form);

    setShowForm(false);
    fetchBookings();
  };

  return (
    <div className="house-container">
      <h1>Bookings</h1>

      {/* ADD BOOKING BUTTON */}
      <div style={{ textAlign: "center", marginBottom: 20 }}>
        <button className="bookings-btn" onClick={() => setShowForm(true)}>
          Add Booking
        </button>
      </div>

      {/* ================= ADD BOOKING FORM ================= */}
      {showForm && (
        <form className="modal-content" onSubmit={submitBooking}>
          <h2>Add Booking</h2>

          {/* PROJECT */}
          <select required onChange={(e) => handleProjectChange(e.target.value)}>
            <option value="">Select Project</option>
            {projects.map(p => (
              <option key={p._id} value={p._id}>
                {p.projectName} ({p.projectType})
              </option>
            ))}
          </select>

          {/* HOUSE */}
          <select required onChange={(e) => handleHouseChange(e.target.value)}>
            <option value="">Select House / Flat</option>
            {houses.map(h => (
              <option key={h._id} value={h._id}>
                {h.houseNumber}
              </option>
            ))}
          </select>

          <input
            placeholder="Customer Name"
            required
            onChange={(e) => setForm({ ...form, customerName: e.target.value })}
          />

          <input
            placeholder="Mobile No"
            required
            maxLength={10}
            onChange={(e) => setForm({ ...form, mobileNo: e.target.value })}
          />

          <select
            onChange={(e) => setForm({ ...form, paymentType: e.target.value })}
          >
            <option value="cash">Cash</option>
            <option value="emi">EMI</option>
          </select>

          <input
            type="number"
            placeholder="Total Payment"
            required
            onChange={(e) => setForm({ ...form, totalPayment: e.target.value })}
          />

          <input
            type="number"
            placeholder="Advance Payment"
            required
            onChange={(e) => setForm({ ...form, advancePayment: e.target.value })}
          />

          {form.paymentType === "emi" && (
            <>
              <input
                type="number"
                placeholder="EMI Months"
                onChange={(e) =>
                  setForm({ ...form, emiMonths: e.target.value })
                }
              />
              <input
                type="date"
                onChange={(e) =>
                  setForm({ ...form, emiStartDate: e.target.value })
                }
              />
            </>
          )}

          <button type="submit">Save Booking</button>
          <button type="button" onClick={() => setShowForm(false)}>
            Cancel
          </button>
        </form>
      )}

      {/* ================= BOOKINGS LIST ================= */}
      <table className="house-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Project</th>
            <th>House</th>
            <th>Total</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map(b => (
            <tr key={b.bookingId}>
              <td>{b.bookingId}</td>
              <td>{b.customerName}</td>
              <td>{b.projectName}</td>
              <td>{b.houseNumber}</td>
              <td>â‚¹{b.totalPayment}</td>
              <td>
                <span className={`status ${b.paymentType}`}>
                  {b.paymentType}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
