import { useEffect, useState } from "react";
import axios from "axios";
import "./Projects.css";

/* ================= CONFIG ================= */
const BASE_URL = "http://localhost:5000"; // backend base url

export default function Projects() {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  /* ================= FETCH PROJECTS ================= */
  const fetchProjects = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/lily`);
      const list = res.data?.data || [];

      // normalize data to avoid runtime errors
      const safeList = list.map((p) => ({
        ...p,
        amenities: Array.isArray(p.amenities) ? p.amenities : [],
        images: Array.isArray(p.images) ? p.images : [],
        floorPlans: Array.isArray(p.floorPlans) ? p.floorPlans : [],
        geoLocation: p.geoLocation || null,
      }));

      setProjects(safeList);
    } catch (err) {
      console.error("Error fetching projects", err);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  /* ================= HELPERS ================= */
  const getLatLng = (geo) => {
    if (
      geo &&
      Array.isArray(geo.coordinates) &&
      geo.coordinates.length === 2
    ) {
      return `${geo.coordinates[1]}, ${geo.coordinates[0]}`;
    }
    return "-";
  };

  /* ================= UI ================= */
  return (
    <div className="projects-wrapper">
      <h2>Project List</h2>

      {/* ================= TABLE ================= */}
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Type</th>
            <th>Location</th>
            <th>Geo (lat,lng)</th>
            <th>Amenities</th>
            <th>Images</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {projects.map((p, i) => (
            <tr key={p.id || i}>
              <td>{i + 1}</td>
              <td>{p.projectName}</td>
              <td>{p.projectType}</td>
              <td>{p.location}</td>
              <td>{getLatLng(p.geoLocation)}</td>
              <td>{p.amenities.join(", ") || "-"}</td>

              <td>
                {p.images.length > 0 ? (
                  <img
                    src={`${BASE_URL}/${p.images[0]}`}
                    alt="preview"
                    className="thumb"
                  />
                ) : (
                  "No Image"
                )}
              </td>

              <td>
                <button
                  className="view-btn"
                  onClick={() => setSelectedProject(p)}
                >
                  View Details
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ================= DETAILS MODAL ================= */}
      {selectedProject && (
        <div className="modal">
          <div className="modal-box">
            <h3>{selectedProject.projectName}</h3>

            <p><b>Type:</b> {selectedProject.projectType}</p>
            <p><b>Location:</b> {selectedProject.location}</p>
            <p>
              <b>Geo:</b> {getLatLng(selectedProject.geoLocation)}
            </p>

            {/* ================= AMENITIES ================= */}
            <p>
              <b>Amenities:</b>{" "}
              {selectedProject.amenities.join(", ") || "-"}
            </p>

            {/* ================= PROJECT IMAGES ================= */}
            <h4>Project Images</h4>
            {selectedProject.images.length > 0 ? (
              <div className="gallery">
                {selectedProject.images.map((img, idx) => (
                  <img
                    key={idx}
                    src={`${BASE_URL}/${img}`}
                    alt="project"
                  />
                ))}
              </div>
            ) : (
              <p>No images uploaded</p>
            )}

            {/* ================= FLOOR PLANS ================= */}
            <h4>Floor Plans</h4>
            {selectedProject.floorPlans.length > 0 ? (
              <div className="gallery">
                {selectedProject.floorPlans.map((fp, idx) => (
                  <img
                    key={idx}
                    src={`${BASE_URL}/${fp}`}
                    alt="floor-plan"
                  />
                ))}
              </div>
            ) : (
              <p>No floor plans uploaded</p>
            )}

            <button
              className="close-btn"
              onClick={() => setSelectedProject(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
