import { useEffect, useState } from "react";
import api from "../api";
import "./Projects.css";

export default function Projects() {
  const [projects, setProjects] = useState([]);
  const [showDetails, setShowDetails] = useState(null);

  /* ================= FETCH PROJECTS ================= */
  const fetchProjects = async () => {
    try {
      const res = await api.get("/lily/");
      // Ensure images & floorPlans are arrays
      const normalized = res.data.data.map((p) => ({
        ...p,
        images: p.images || [],
        floorPlans: p.floorPlans || [],
        amenities: p.amenities || [],
        geoLocation: p.geoLocation || { coordinates: [] },
      }));
      setProjects(normalized);
    } catch (err) {
      console.error("Failed to fetch projects", err);
      setProjects([]);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Delete this project?")) {
      await api.delete(`/lily/${id}`);
      fetchProjects();
    }
  };

  /* ================= UI ================= */
  return (
    <div className="projects-page">
      <h1>Project Management</h1>

      {/* ================= TABLE ================= */}
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Type</th>
            <th>Location</th>
            <th>Geo (lat,lng)</th>
            <th>Wings/Floors/Plots</th>
            <th>Amenities</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((p, i) => (
            <tr key={p.id}>
              <td>{i + 1}</td>
              <td>{p.projectName}</td>
              <td>{p.projectType}</td>
              <td>{p.location}</td>
              <td>
                {p.geoLocation.coordinates?.length
                  ? `${p.geoLocation.coordinates[1]}, ${p.geoLocation.coordinates[0]}`
                  : "-"}
              </td>
              <td>
                {p.projectType === "flat"
                  ? `${p.totalWings || 0} Wings / ${p.totalFloors || 0} Floors / ${
                      p.perFloorHouse || 0
                    } per Floor`
                  : p.totalPlots || 0}
              </td>
              <td>{(p.amenities || []).join(", ")}</td>
              <td>
                <button onClick={() => setShowDetails(p)}>View Details</button>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ================= VIEW DETAILS MODAL ================= */}
      {showDetails && (
        <div className="modal">
          <div className="modal-content" style={{ maxHeight: "80vh", overflowY: "auto" }}>
            <h2>{showDetails.projectName} - Details</h2>
            <hr />
            <p><b>Type:</b> {showDetails.projectType}</p>
            <p><b>Location:</b> {showDetails.location}</p>
            <p>
              <b>Geo (lat,lng):</b>{" "}
              {showDetails.geoLocation.coordinates?.length
                ? `${showDetails.geoLocation.coordinates[1]}, ${showDetails.geoLocation.coordinates[0]}`
                : "-"}
            </p>
            {showDetails.projectType === "flat" ? (
              <p>
                <b>Wings/Floors/Houses:</b> {showDetails.totalWings} / {showDetails.totalFloors} / {showDetails.perFloorHouse}
              </p>
            ) : (
              <p><b>Total Plots:</b> {showDetails.totalPlots}</p>
            )}
            <p><b>Amenities:</b> {(showDetails.amenities || []).join(", ")}</p>

            {/* Project Images */}
            {showDetails.images?.length > 0 && (
              <div>
                <h4>Project Images</h4>
                <div className="image-gallery">
                  {showDetails.images.map((img, idx) => (
                    <img key={idx} src={img} alt="project" />
                  ))}
                </div>
              </div>
            )}

            {/* Floor Plans */}
            {showDetails.floorPlans?.length > 0 && (
              <div>
                <h4>Floor Plans</h4>
                <div className="image-gallery">
                  {showDetails.floorPlans.map((fp, idx) => (
                    <img key={idx} src={fp} alt="floor plan" />
                  ))}
                </div>
              </div>
            )}

            <button className="btn-close" onClick={() => setShowDetails(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
