import { useEffect, useState } from "react";
import api from "../api";

export default function Bookings() {
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);

  const [form, setForm] = useState({
    projectId: "",
    projectName: "",
    projectType: "",
    perHouseCost: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
  });

  useEffect(() => {
    api.get("/lily").then(res => setProjects(res.data.data));
  }, []);

  const handleProjectChange = async (pid) => {
    const project = projects.find(p => String(p.id) === pid);
    if (!project) return;

    setForm({
      ...form,
      projectId: project.id,
      projectName: project.projectName,
      projectType: project.projectType,
      perHouseCost: project.perHouseCost,
      houseNumber: "",
    });

    const res = await api.get(`/lily/houses/${project.id}`);
    setHouses(res.data.data.filter(h => h.status === "available"));
  };

  const submitBooking = async (e) => {
    e.preventDefault();
    await api.post("/bookings/create", form);
    alert("Booking created");
  };

  return (
    <form onSubmit={submitBooking}>
      <select required onChange={e => handleProjectChange(e.target.value)}>
        <option value="">Select Project</option>
        {projects.map(p => (
          <option key={p.id} value={p.id}>
            {p.projectName}
          </option>
        ))}
      </select>

      <select required onChange={e => setForm({ ...form, houseNumber: e.target.value })}>
        <option value="">Select House</option>
        {houses.map(h => (
          <option key={h.houseNumber} value={h.houseNumber}>
            {h.houseNumber}
          </option>
        ))}
      </select>

      <input placeholder="Customer Name" onChange={e => setForm({ ...form, customerName: e.target.value })} />
      <input placeholder="Mobile" onChange={e => setForm({ ...form, mobileNo: e.target.value })} />

      <input placeholder="Total Payment" onChange={e => setForm({ ...form, totalPayment: e.target.value })} />
      <input placeholder="Advance Payment" onChange={e => setForm({ ...form, advancePayment: e.target.value })} />

      <button type="submit">Book</button>
    </form>
  );
}
