import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

const ITEMS_PER_PAGE = 20;

export default function Houses() {
  const [projects, setProjects] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [houses, setHouses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);

  const [modalHouse, setModalHouse] = useState(null);
  const [actionType, setActionType] = useState("");
  const [formData, setFormData] = useState({
    customerName: "",
    customerPhone: "",
    amount: ""
  });

  /* ---------------- Fetch Projects ---------------- */
  useEffect(() => {
    api
      .get("/lily/")
      .then((res) =>
        setProjects(Array.isArray(res?.data?.data) ? res.data.data : [])
      )
      .catch(() => alert("Failed to fetch projects"));
  }, []);

  /* ---------------- Selected Project ---------------- */
  const selectedProject = projects.find((p) => p.id === selectedId);

  /* ---------------- Fetch Houses ---------------- */
  const handleChange = async (id) => {
    setSelectedId(id);
    setCurrentPage(1);

    if (!id) {
      setHouses([]);
      return;
    }

    try {
      setLoading(true);
      const res = await api.get(`/lily/houses/${id}`);
      setHouses(Array.isArray(res?.data?.data) ? res.data.data : []);
    } catch {
      alert("Failed to load houses");
      setHouses([]);
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- Modal Logic ---------------- */
  const openModal = (house, type) => {
    setModalHouse(house);
    setActionType(type);
    setFormData({ customerName: "", customerPhone: "", amount: "" });
  };

  const closeModal = () => {
    setModalHouse(null);
    setActionType("");
    setFormData({ customerName: "", customerPhone: "", amount: "" });
  };

  const submitForm = async () => {
    if (!formData.customerName || !formData.customerPhone || !formData.amount) {
      alert("Please fill all fields");
      return;
    }

    try {
      const payload = {
        action: actionType,
        customerName: formData.customerName,
        customerPhone: formData.customerPhone,
        amount: Number(formData.amount)
      };

      const res = await api.patch(
        `/lily/${selectedId}/${modalHouse.houseNumber}`,
        payload
      );

      setHouses((prev) =>
        prev.map((h) =>
          h.houseNumber === modalHouse.houseNumber ? res.data.data : h
        )
      );

      closeModal();
    } catch {
      alert("Failed to update house");
    }
  };

  /* ---------------- Pagination ---------------- */
  const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
  const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
  const currentHouses = houses.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(houses.length / ITEMS_PER_PAGE);

  const paginate = (page) => {
    if (page > 0 && page <= totalPages) {
      setCurrentPage(page);
      window.scrollTo(0, 0);
    }
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="house-container">
      <h1>Select Project</h1>

      {/* Project Dropdown */}
      <select
  className="project-select"
  value={selectedId}
  onChange={(e) => handleChange(e.target.value)}
>
  <option value="">Select Project</option>
  {projects.map((p) => (
    <option key={p.id} value={p.id}>
      {p.projectName} ({p.projectType})
    </option>
  ))}
</select>


      {/* Project Type Display */}
      {selectedProject && (
        <p style={{ textAlign: "center", fontWeight: "600", marginBottom: "20px" }}>
          Project Type:{" "}
          <span style={{ textTransform: "capitalize" }}>
            {selectedProject.projectType}
          </span>
        </p>
      )}

      {loading && <p className="loading">Loading...</p>}

      {!loading && selectedId && currentHouses.length === 0 && (
        <p className="no-data">No houses found</p>
      )}

      {/* House Table */}
      {currentHouses.length > 0 && (
        <>
          <table className="house-table">
            <thead>
              <tr>
                {/* <th>S. No.</th> */}
                {/* <th>Project Name</th> */}
                <th>House No</th>
                {/* <th>Project Type</th> */}
                <th>Square Feet</th>
                <th>Price (₹)</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {currentHouses.map((h, i) => (
                <tr key={h.houseNumber}>
                  {/* <td>{indexOfFirstItem + i + 1}</td> */}
                  {/* <td>{h.projectName}</td> */}
                  <td>{h.houseNumber}</td>
                  {/* <td>{h.projectType}</td> */}
                  <td>{h.squareFeet}</td>
                  <td>₹{Number(h.price || 0).toLocaleString()}</td>
                  <td>
                    <span className={`status ${h.status}`}>
                      {h.status}
                    </span>
                  </td>
                  <td>
                    {h.status === "available" && (
                      <>
                        <button onClick={() => openModal(h, "book")}>Book</button>
                        <button onClick={() => openModal(h, "sell")}>Sell</button>
                      </>
                    )}
                    {h.status !== "available" && <span>-</span>}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="pagination">
              <button
                className="pagination-btn"
                disabled={currentPage === 1}
                onClick={() => paginate(currentPage - 1)}
              >
                Prev
              </button>

              <span className="pagination-info">
                Page {currentPage} of {totalPages}
              </span>

              <button
                className="pagination-btn"
                disabled={currentPage === totalPages}
                onClick={() => paginate(currentPage + 1)}
              >
                Next
              </button>
            </div>
          )}
        </>
      )}

      {/* Modal */}
      {modalHouse && (
        <div className="modal">
          <div className="modal-content">
            <h2>
              {actionType === "book" ? "Book House" : "Sell House"} -{" "}
              {modalHouse.houseNumber}
            </h2>

            <form
              onSubmit={(e) => {
                e.preventDefault();
                submitForm();
              }}
            >
              <input
                type="text"
                placeholder="Customer Name"
                value={formData.customerName}
                onChange={(e) =>
                  setFormData({ ...formData, customerName: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Customer Phone"
                value={formData.customerPhone}
                onChange={(e) =>
                  setFormData({ ...formData, customerPhone: e.target.value })
                }
              />
              <input
                type="number"
                placeholder={
                  actionType === "book" ? "Advance Amount" : "Total Amount"
                }
                value={formData.amount}
                onChange={(e) =>
                  setFormData({ ...formData, amount: e.target.value })
                }
              />
              <button type="submit">Submit</button>
              <button type="button" onClick={closeModal}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
