import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

export default function Bookings() {
  const today = new Date().toISOString().split("T")[0];

  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    projectId: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
    incompletePayment: 0,
    bookingDate: today,
  });

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadProjects();
    loadBookings();
  }, []);

  const loadProjects = async () => {
    const res = await api.get("/lily");
    setProjects(res.data.data);
  };

  const loadBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  /* ================= PROJECT ID → NAME MAP ================= */
  const projectMap = {};
  projects.forEach(p => {
    projectMap[p.id] = p.projectName;
  });

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    setForm({
      ...form,
      projectId: pid,
      houseNumber: "",
      totalPayment: "",
      advancePayment: "",
      incompletePayment: 0,
    });

    try {
      const res = await api.get(`/lily/houses/${pid}`);
      setHouses(res.data.data.filter(h => h.status === "available"));
    } catch {
      setHouses([]);
    }
  };

  /* ================= HOUSE CHANGE ================= */
  const handleHouseChange = (houseNo) => {
    const house = houses.find(h => h.houseNumber === houseNo);
    const price = house?.price || 0;

    setForm(prev => ({
      ...prev,
      houseNumber: houseNo,
      totalPayment: price,
      incompletePayment:
        prev.paymentType === "emi"
          ? price - Number(prev.advancePayment || 0)
          : 0,
    }));
  };

  /* ================= PAYMENT TYPE ================= */
  const handlePaymentType = (type) => {
    setForm(prev => ({
      ...prev,
      paymentType: type,
      incompletePayment:
        type === "emi"
          ? Number(prev.totalPayment || 0) - Number(prev.advancePayment || 0)
          : 0,
    }));
  };

  /* ================= ADVANCE ================= */
  const handleAdvance = (value) => {
    setForm(prev => ({
      ...prev,
      advancePayment: value,
      incompletePayment:
        prev.paymentType === "emi"
          ? Number(prev.totalPayment || 0) - Number(value || 0)
          : 0,
    }));
  };

  /* ================= SUBMIT ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        ...form,
        projectId: Number(form.projectId),
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment),
        incompletePayment: Number(form.incompletePayment),
      });

      alert("✅ Booking Created");

      setForm({
        projectId: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: "",
        advancePayment: "",
        incompletePayment: 0,
        bookingDate: today,
      });

      setHouses([]);
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ Booking Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div className="booking-container">
      <h2 className="page-title">Create Booking</h2>

      <form onSubmit={submitBooking}>
        <select
          required
          value={form.projectId}
          onChange={e => handleProjectChange(e.target.value)}
        >
          <option value="">Select Project</option>
          {projects.map(p => (
            <option key={p.id} value={p.id, p.projectName}>
              {p.projectName}
            </option>
          ))}
        </select>

        <select
          required
          value={form.houseNumber}
          onChange={e => handleHouseChange(e.target.value)}
        >
          <option value="">Select House</option>
          {houses.map(h => (
            <option key={h.houseNumber} value={h.houseNumber}>
              {h.houseNumber}
            </option>
          ))}
        </select>

        <input
          required
          placeholder="Customer Name"
          value={form.customerName}
          onChange={e => setForm({ ...form, customerName: e.target.value })}
        />

        <input
          required
          placeholder="Mobile Number"
          value={form.mobileNo}
          onChange={e => setForm({ ...form, mobileNo: e.target.value })}
        />

        <select
          value={form.paymentType}
          onChange={e => handlePaymentType(e.target.value)}
        >
          <option value="cash">Cash</option>
          <option value="emi">EMI</option>
        </select>

        <input
          type="number"
          readOnly
          placeholder="Total Amount"
          value={form.totalPayment}
        />

        <input
          type="number"
          required
          placeholder="Advance Payment"
          value={form.advancePayment}
          onChange={e => handleAdvance(e.target.value)}
        />

        {form.paymentType === "emi" && (
          <input
            type="number"
            readOnly
            placeholder="Pending Amount"
            value={form.incompletePayment}
          />
        )}

        <input
          type="date"
          value={form.bookingDate}
          onChange={e => setForm({ ...form, bookingDate: e.target.value })}
        />

        <button disabled={loading}>
          {loading ? "Booking..." : "Book House"}
        </button>
      </form>

      <h2 className="page-title">Booking List</h2>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Project</th>
            <th>House</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Total</th>
            <th>Advance</th>
            <th>Pending</th>
            <th>Date</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b => (
            <tr key={b._id}>
              <td>{b.bookingId}</td>

              {/* ✅ PROJECT NAME SHOWN */}
              <td>{projectMap[b.projectName] || "N/A"}</td>

              <td>{b.houseNumber}</td>
              <td>{b.customerName}</td>

              <td className={`badge ${b.paymentType}`}>
                {b.paymentType.toUpperCase()}
              </td>

              <td>₹{b.totalPayment}</td>
              <td>₹{b.advancePayment}</td>

              <td>
                {b.paymentType === "emi"
                  ? `₹${b.incompletePayment}`
                  : "Paid"}
              </td>

              <td>{new Date(b.bookingDate).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
