import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

export default function Bookings() {
  const today = new Date().toISOString().split("T")[0];

  /* ================= STATES ================= */
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const [payAmount, setPayAmount] = useState("");

  const [form, setForm] = useState({
  projectId: "",
  houseNumber: "",
  customerName: "",
  mobileNo: "",
  paymentType: "cash",
  totalPayment: "",
  advancePayment: "",
  pendingAmount: "",     // ✅ ADD THIS
  emiMonths: "",
  monthlyEmi: "",
  bookingDate: today,
});

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadProjects();
    loadBookings();
  }, []);

  useEffect(() => {
  if (form.paymentType === "emi" && form.emiMonths && form.totalPayment) {
    const remaining = Number(form.totalPayment) - Number(form.advancePayment);
    const emi = remaining / Number(form.emiMonths);
    setForm(prev => ({ ...prev, monthlyEmi: emi }));
  }
}, [form.emiMonths, form.totalPayment, form.advancePayment, form.paymentType]);


  const loadProjects = async () => {
    const res = await api.get("/lily");
    setProjects(res.data.data);
  };

  const loadBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  /* ================= PROJECT MAP ================= */
  const projectMap = {};
  projects.forEach(p => (projectMap[p.id] = p.projectName));

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    setForm({
      ...form,
      projectId: pid,
      houseNumber: "",
      totalPayment: "",
      advancePayment: "",
      emiMonths: "",
      monthlyEmi: "",
    });

    try {
      const res = await api.get(`/lily/houses/${pid}`);
      setHouses(res.data.data.filter(h => h.status === "available"));
    } catch {
      setHouses([]);
    }
  };

  /* ================= HOUSE CHANGE ================= */
 const handleHouseChange = (houseNo) => {
  const house = houses.find(h => h.houseNumber === houseNo);
  const price = house?.price || "";

  setForm(prev => ({
    ...prev,
    houseNumber: houseNo,
    totalPayment: price,
    pendingAmount: price - Number(prev.advancePayment || ""),
  }));
};

  /* ================= SUBMIT BOOKING ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        projectId: Number(form.projectId),
        houseNumber: form.houseNumber,
        customerName: form.customerName,
        mobileNo: form.mobileNo,
        paymentType: form.paymentType,
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment || ""),
        pendingAmount: Number(form.pendingAmount), // optional
        // ✅ EMI sent only if needed
        emiMonths: form.paymentType === "emi" ? Number(form.emiMonths) : "",
        monthlyEmi: form.paymentType === "emi" ? Number(form.monthlyEmi) : "",
      });

      alert("✅ Booking Created Successfully");

      setForm({
        projectId: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: "",
        advancePayment: "",
        emiMonths: "",
        monthlyEmi: "",
        bookingDate: today,
      });

      setHouses([]);
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ Booking Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= PAY EMI ================= */
  const payEmi = async (bookingId, monthNo) => {
    try {
      await api.post("/bookings/emi/pay", { bookingId, monthNo });
      alert("✅ EMI Paid");
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ EMI Payment Failed");
    }
  };

  /* ================= PAY CASH ================= */
  const payCash = async (bookingId) => {
    if (!payAmount) return alert("Enter amount");

    try {
      await api.post("/bookings/cash/pay", {
        bookingId,
        amount: Number(payAmount),
      });
      alert("✅ Cash Payment Received");
      setPayAmount("");
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ Cash Payment Failed");
    }
  };

  /* ================= UI ================= */
  return (
    <div className="booking-container">
      <h2 className="page-title">Create Booking</h2>

      <form onSubmit={submitBooking} className="booking-form">
        <select required value={form.projectId} onChange={e => handleProjectChange(e.target.value)}>
          <option value="">Select Project</option>
          {projects.map(p => (
            <option key={p.id} value={p.id}>{p.projectName}</option>
          ))}
        </select>

        <select required value={form.houseNumber} onChange={e => handleHouseChange(e.target.value)}>
          <option value="">Select House</option>
          {houses.map(h => (
            <option key={h.houseNumber} value={h.houseNumber}>{h.houseNumber}</option>
          ))}
        </select>

        <input required placeholder="Customer Name"
          value={form.customerName}
          onChange={e => setForm({ ...form, customerName: e.target.value })}
        />

        <input required placeholder="Mobile Number" type="tel" maxLength={10}
          value={form.mobileNo}
          onChange={e => setForm({ ...form, mobileNo: e.target.value })}
        />

        <select value={form.paymentType}
          onChange={e => setForm({ ...form, paymentType: e.target.value })}>
          <option value="cash">Cash</option>
          <option value="emi">EMI</option>
        </select>

        <input readOnly type="text" placeholder="Total Amount" value={form.totalPayment} />

        <input
  type="text"
  required
  placeholder="Advance Payment"
  value={form.advancePayment}
  onChange={e => {
    const adv = Number(e.target.value || "");
    setForm(prev => ({
      ...prev,
      advancePayment: adv,
      pendingAmount: Number(prev.totalPayment || "") - adv,
    }));
  }}
/>

<input
  type="number"
  readOnly
  placeholder="Pending Amount"
  value={form.pendingAmount}
/>

        {/* ✅ EMI INPUTS */}
        {form.paymentType === "emi" && (
          <>
            <input 
      type="text" 
      placeholder="EMI Months"
      value={form.emiMonths}
      onChange={e => setForm({ ...form, emiMonths: e.target.value })}
      required
    />
    <input 
      type="text" 
      readOnly 
      placeholder="Monthly EMI"
      value={form.monthlyEmi}
    />

    <input
  type="date"
  value={form.bookingDate}
  max={today}
  onChange={e =>
    setForm({ ...form, bookingDate: e.target.value })
  }
/>
  </>

  

        )}

        <button disabled={loading}>
          {loading ? "Booking..." : "Book House"}
        </button>
      </form>

      <h2 className="page-title">Booking List</h2>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Project</th>
            <th>House</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Total</th>
            <th>Advance</th>
            <th>Pending</th>
            <th>Payment</th>
            <th>Date</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b => (
            <tr key={b._id}>
              <td>{b.bookingId}</td>
              <td>{projectMap[b.projectId]}</td>
              <td>{b.houseNumber}</td>
              <td>{b.customerName}</td>
              <td className={`badge ${b.paymentType}`}>{b.paymentType.toUpperCase()}</td>
              <td>₹{b.totalPayment}</td>
              <td>₹{b.advancePayment}</td>
              <td>₹{b.pendingAmount}</td>

              <td>
                {b.pendingAmount === "" && <b style={{ color: "green" }}>PAID</b>}

                {b.paymentType === "cash" && b.pendingAmount > "" && (
                  <>
                    <input
                      type="number"
                      placeholder="Amount"
                      style={{ width: 80 }}
                      value={payAmount}
                      onChange={e => setPayAmount(e.target.value)}
                    />
                    <button onClick={() => payCash(b.bookingId)}>Pay</button>
                  </>
                )}

                {b.paymentType === "emi" && (
                  <select onChange={e => payEmi(b.bookingId, Number(e.target.value))}>
                    <option value="">Pay EMI</option>
                    {b.emiSchedule?.filter(e => e.status === "pending")
                      .map(e => (
                        <option key={e.monthNo} value={e.monthNo}>
                          Month {e.monthNo} – ₹{e.amount}
                        </option>
                      ))}
                  </select>
                )}
              </td>

              <td>{new Date(b.bookingDate).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
