import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import "./House.css";

/* ================= INR FORMATTER (GLOBAL) ================= */
const formatINR = (value) => {
  if (value === null || value === undefined || value === "") return "0";
  return new Intl.NumberFormat("en-IN").format(Number(value));
};

export default function Bookings() {
  const navigate = useNavigate();
  const today = new Date().toISOString().split("T")[0];

  /* ================= STATES ================= */
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    projectId: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
    pendingAmount: "",
    bookingDate: today,
  });

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadProjects();
    loadBookings();
  }, []);

  const loadProjects = async () => {
    const res = await api.get("/lily");
    setProjects(res.data.data);
  };

  const loadBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  const projectMap = {};
  projects.forEach((p) => (projectMap[p.id] = p.projectName));

  const handleProjectChange = async (pid) => {
    setForm({
      ...form,
      projectId: pid,
      houseNumber: "",
      totalPayment: "",
      advancePayment: "",
      pendingAmount: "",
    });

    const res = await api.get(`/lily/houses/${pid}`);
    setHouses(res.data.data.filter((h) => h.status === "available"));
  };

  const handleHouseChange = (houseNo) => {
    const house = houses.find((h) => h.houseNumber === houseNo);
    const price = house?.price || 0;

    setForm((prev) => ({
      ...prev,
      houseNumber: houseNo,
      totalPayment: price,
      pendingAmount: price - Number(prev.advancePayment || 0),
    }));
  };

  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    await api.post("/bookings/create", {
      projectId: Number(form.projectId),
      houseNumber: form.houseNumber,
      customerName: form.customerName,
      mobileNo: form.mobileNo,
      paymentType: form.paymentType,
      totalPayment: Number(form.totalPayment),
      advancePayment: Number(form.advancePayment),
      pendingAmount: Number(form.pendingAmount),
    });

    alert("✅ Booking Created");

    setForm({
      projectId: "",
      houseNumber: "",
      customerName: "",
      mobileNo: "",
      paymentType: "cash",
      totalPayment: "",
      advancePayment: "",
      pendingAmount: "",
      bookingDate: today,
    });

    setHouses([]);
    loadBookings();
    setLoading(false);
  };

  return (
    <div className="booking-container">
      <h2 className="page-title">Create Booking</h2>

      <form onSubmit={submitBooking} className="booking-form">
        <select required value={form.projectId} onChange={(e) => handleProjectChange(e.target.value)}>
          <option value="">Select Project</option>
          {projects.map((p) => (
            <option key={p.id} value={p.id}>{p.projectName}</option>
          ))}
        </select>

        <select required value={form.houseNumber} onChange={(e) => handleHouseChange(e.target.value)}>
          <option value="">Select House</option>
          {houses.map((h) => (
            <option key={h.houseNumber} value={h.houseNumber}>{h.houseNumber}</option>
          ))}
        </select>

        <input placeholder="Customer Name" required value={form.customerName}
          onChange={(e) => setForm({ ...form, customerName: e.target.value })} />

        <input placeholder="Mobile No" required maxLength={10} value={form.mobileNo}
          onChange={(e) => setForm({ ...form, mobileNo: e.target.value })} />

        <input readOnly value={`₹${formatINR(form.totalPayment)}`} />
        <input placeholder="Booking Amount" required
          onChange={(e) => {
            const adv = Number(e.target.value || 0);
            setForm((prev) => ({
              ...prev,
              advancePayment: adv,
              pendingAmount: Number(prev.totalPayment || 0) - adv,
            }));
          }} />

        <input readOnly value={`₹${formatINR(form.pendingAmount)}`} />

        <button disabled={loading}>{loading ? "Booking..." : "Book House"}</button>
      </form>

      <h2 className="page-title">Booking List</h2>

      <table>
        <tbody>
          {bookings.map((b) => (
            <tr key={b._id}>
              <td>{b.customerName}</td>
              <td>₹{formatINR(b.totalPayment)}</td>
              <td>
                {b.pendingAmount === 0 ? "SOLD" : `₹${formatINR(b.pendingAmount)}`}
              </td>
              <td>
                <button onClick={() => navigate(`/booking-history/${b._id}`)}>
                  History
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
