import { useEffect, useState } from "react";
import api from "../api";
import "./House.css";

export default function Bookings() {
  const today = new Date().toISOString().split("T")[0];

  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    projectId: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
    incompletePayment: 0,
    emiMonths: 0,
    monthlyEmi: 0,
    bookingDate: today,
  });

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadProjects();
    loadBookings();
  }, []);

  const loadProjects = async () => {
    const res = await api.get("/lily");
    setProjects(res.data.data);
  };

  const loadBookings = async () => {
    const res = await api.get("/bookings");
    setBookings(res.data.data);
  };

  /* ================= PROJECT MAP ================= */
  const projectMap = {};
  projects.forEach((p) => (projectMap[p.id] = p.projectName));

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    setForm({
      ...form,
      projectId: pid,
      houseNumber: "",
      totalPayment: "",
      advancePayment: "",
      incompletePayment: 0,
      emiMonths: 0,
      monthlyEmi: 0,
    });

    try {
      const res = await api.get(`/lily/houses/${pid}`);
      setHouses(res.data.data.filter((h) => h.status === "available"));
    } catch {
      setHouses([]);
    }
  };

  /* ================= HOUSE CHANGE ================= */
  const handleHouseChange = (houseNo) => {
    const house = houses.find((h) => h.houseNumber === houseNo);
    const price = house?.price || 0;

    setForm((prev) => ({
      ...prev,
      houseNumber: houseNo,
      totalPayment: price,
      incompletePayment:
        prev.paymentType === "emi"
          ? price - Number(prev.advancePayment || 0)
          : price - Number(prev.advancePayment || 0),
    }));
  };

  /* ================= PAYMENT TYPE ================= */
  const handlePaymentType = (type) => {
    setForm((prev) => ({
      ...prev,
      paymentType: type,
      emiMonths: 0,
      monthlyEmi: 0,
      incompletePayment:
        Number(prev.totalPayment || 0) - Number(prev.advancePayment || 0),
    }));
  };

  /* ================= ADVANCE ================= */
  const handleAdvance = (value) => {
    setForm((prev) => {
      const pending = Number(prev.totalPayment || 0) - Number(value || 0);

      return {
        ...prev,
        advancePayment: value,
        incompletePayment: pending,
        monthlyEmi:
          prev.paymentType === "emi" && prev.emiMonths > 0
            ? Math.ceil(pending / prev.emiMonths)
            : 0,
      };
    });
  };

  /* ================= EMI MONTHS ================= */
  const handleEmiMonths = (months) => {
    const pending = Number(form.incompletePayment || 0);

    setForm((prev) => ({
      ...prev,
      emiMonths: months,
      monthlyEmi: months > 0 ? Math.ceil(pending / months) : 0,
    }));
  };

  /* ================= SUBMIT ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        ...form,
        projectId: Number(form.projectId),
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment),
        incompletePayment: Number(form.incompletePayment),
        emiMonths: Number(form.emiMonths),
        monthlyEmi: Number(form.monthlyEmi),
      });

      alert("✅ Booking Created");

      setForm({
        projectId: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: "",
        advancePayment: "",
        incompletePayment: 0,
        emiMonths: 0,
        monthlyEmi: 0,
        bookingDate: today,
      });

      setHouses([]);
      loadBookings();
    } catch (err) {
      alert(err.response?.data?.message || "❌ Booking Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= ACTIONS ================= */
  const markCashPaid = async (id) => {
    try {
      await api.post(`/bookings/mark-cash-paid/${id}`);
      loadBookings();
    } catch (err) {
      console.error(err);
      alert("Failed to mark cash paid");
    }
  };

  const payEmi = async (bookingId, monthNo) => {
    try {
      await api.post("/bookings/pay-emi", { bookingId, monthNo });
      loadBookings();
    } catch (err) {
      console.error(err);
      alert("EMI payment failed");
    }
  };

  /* ================= UI ================= */
  return (
    <div className="booking-container">
      <h2>Create Booking</h2>

      <form onSubmit={submitBooking}>
        <select required value={form.projectId} onChange={(e) => handleProjectChange(e.target.value)}>
          <option value="">Select Project</option>
          {projects.map((p) => (
            <option key={p.id} value={p.id}>{p.projectName}</option>
          ))}
        </select>

        <select required value={form.houseNumber} onChange={(e) => handleHouseChange(e.target.value)}>
          <option value="">Select House</option>
          {houses.map((h) => (
            <option key={h.houseNumber} value={h.houseNumber}>{h.houseNumber}</option>
          ))}
        </select>

        <input placeholder="Customer Name" required value={form.customerName}
          onChange={(e) => setForm({ ...form, customerName: e.target.value })} />

        <input placeholder="Mobile No" required value={form.mobileNo}
          onChange={(e) => setForm({ ...form, mobileNo: e.target.value })} />

        <select value={form.paymentType} onChange={(e) => handlePaymentType(e.target.value)}>
          <option value="cash">Cash</option>
          <option value="emi">EMI</option>
        </select>

        <input readOnly placeholder="Total Amount" value={form.totalPayment} />
        <input type="number" placeholder="Advance Payment" required
          value={form.advancePayment} onChange={(e) => handleAdvance(e.target.value)} />

        {form.paymentType === "emi" && (
          <>
            <input type="number" placeholder="EMI Months"
              value={form.emiMonths} onChange={(e) => handleEmiMonths(e.target.value)} />
            <input readOnly placeholder="Monthly EMI" value={form.monthlyEmi} />
          </>
        )}

        <input readOnly placeholder="Pending Amount" value={form.incompletePayment} />

        <button disabled={loading}>{loading ? "Saving..." : "Book House"}</button>
      </form>

      <h2>Booking List</h2>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Project</th>
            <th>House</th>
            <th>Customer</th>
            <th>Type</th>
            <th>Total</th>
            <th>Advance</th>
            <th>Pending</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map((b) => {
            const cashPending = b.paymentType === "cash" && b.totalPayment > b.advancePayment;

            return (
              <tr key={b._id}>
                <td>{b.bookingId}</td>
                <td>{projectMap[b.projectId]}</td>
                <td>{b.houseNumber}</td>
                <td>{b.customerName}</td>
                <td>{b.paymentType.toUpperCase()}</td>
                <td>₹{b.totalPayment}</td>
                <td>₹{b.advancePayment}</td>
                <td>₹{b.incompletePayment}</td>
                <td>
                  {/* Cash pending */}
                  {cashPending && (
                    <button onClick={() => markCashPaid(b.bookingId)}>Mark Cash Paid</button>
                  )}

                  {/* EMI pending */}
                  {b.paymentType === "emi" &&
                    b.emiSchedule?.map((e) => (
                      e.status === "pending" ? (
                        <button key={e.monthNo} onClick={() => payEmi(b.bookingId, e.monthNo)}>
                          Pay EMI Month {e.monthNo} (₹{e.amount})
                        </button>
                      ) : (
                        <span key={e.monthNo}>Month {e.monthNo} Paid </span>
                      )
                    ))
                  }

                  {!cashPending && b.paymentType === "cash" && "Completed"}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
