import { useEffect, useState } from "react";
import api from "../api";

export default function Bookings() {
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    projectId: "",
    projectName: "",
    projectType: "",
    perHouseCost: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",   // cash | emi
    totalPayment: "",
    advancePayment: "",
    emiMonths: "",         // EMI only
  });

  /* ================= LOAD PROJECTS ================= */
  useEffect(() => {
    api
      .get("/lily")
      .then((res) => setProjects(res.data.data))
      .catch((err) => console.error(err));
  }, []);

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    const project = projects.find((p) => String(p.id) === pid);
    if (!project) return;

    setForm((prev) => ({
      ...prev,
      projectId: project.id,
      projectName: project.projectName,
      projectType: project.projectType,
      perHouseCost: project.perHouseCost,
      houseNumber: "",
    }));

    try {
      const res = await api.get(`/lily/houses/${project.id}`);
      setHouses(res.data.data.filter((h) => h.status === "available"));
    } catch (err) {
      console.error(err);
      setHouses([]);
    }
  };

  /* ================= SUBMIT BOOKING ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        ...form,
        perHouseCost: Number(form.perHouseCost),
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment),
        emiMonths: form.paymentType === "emi" ? Number(form.emiMonths) : 0,
      });

      alert("✅ Booking created successfully");

      setForm({
        projectId: "",
        projectName: "",
        projectType: "",
        perHouseCost: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: "",
        advancePayment: "",
        emiMonths: "",
      });

      setHouses([]);
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "❌ Booking failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 500, margin: "40px auto" }}>
      <h2>Create Booking</h2>

      <form onSubmit={submitBooking}>
        {/* PROJECT */}
        <select
          required
          value={form.projectId}
          onChange={(e) => handleProjectChange(e.target.value)}
        >
          <option value="">Select Project</option>
          {projects.map((p) => (
            <option key={p.id} value={p.id}>
              {p.projectName}
            </option>
          ))}
        </select>

        {/* HOUSE */}
        <select
          required
          value={form.houseNumber}
          onChange={(e) =>
            setForm({ ...form, houseNumber: e.target.value })
          }
        >
          <option value="">Select House</option>
          {houses.map((h) => (
            <option key={h.houseNumber} value={h.houseNumber}>
              House {h.houseNumber}
            </option>
          ))}
        </select>

        {/* CUSTOMER */}
        <input
          required
          placeholder="Customer Name"
          value={form.customerName}
          onChange={(e) =>
            setForm({ ...form, customerName: e.target.value })
          }
        />

        <input
          required
          placeholder="Mobile Number"
          value={form.mobileNo}
          onChange={(e) =>
            setForm({ ...form, mobileNo: e.target.value })
          }
        />

        {/* PAYMENT TYPE */}
        <select
          value={form.paymentType}
          onChange={(e) =>
            setForm({ ...form, paymentType: e.target.value })
          }
        >
          <option value="cash">Cash</option>
          <option value="emi">EMI</option>
        </select>

        {/* TOTAL */}
        <input
          required
          type="number"
          placeholder="Total Amount"
          value={form.totalPayment}
          onChange={(e) =>
            setForm({ ...form, totalPayment: e.target.value })
          }
        />

        {/* ADVANCE */}
        <input
          required
          type="number"
          placeholder="Advance Payment"
          value={form.advancePayment}
          onChange={(e) =>
            setForm({ ...form, advancePayment: e.target.value })
          }
        />

        {/* EMI MONTHS */}
        {form.paymentType === "emi" && (
          <input
            required
            type="number"
            placeholder="EMI Months"
            value={form.emiMonths}
            onChange={(e) =>
              setForm({ ...form, emiMonths: e.target.value })
            }
          />
        )}

        <button type="submit" disabled={loading}>
          {loading ? "Booking..." : "Book House"}
        </button>
      </form>
    </div>
  );
}
