import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";
import "./House.css";

export default function BookingHistory() {
  const { bookingId } = useParams();

  const [history, setHistory] = useState([]);
  const [booking, setBooking] = useState(null);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    amountReceived: "",
    paymentMethod: "cash",
    paymentReceivedDate: "",
  });

  /* ================= LOAD DATA ================= */
  useEffect(() => {
    loadBooking();
    loadHistory();
  }, []);

  /* LOAD BOOKING (TOTAL AMOUNT) */
  const loadBooking = async () => {
    const res = await api.get(`/bookings/${bookingId}`);
    setBooking(res.data.data);
  };

  /* LOAD PAYMENT HISTORY */
  const loadHistory = async () => {
    const res = await api.get(`/payment-history?bookingId=${bookingId}`);
    setHistory(res.data.data || res.data);
  };

  /* ================= CALCULATIONS ================= */
  const totalPaid = history.reduce(
    (sum, p) => sum + Number(p.amountReceived || 0),
    0
  );

  const totalAmount = booking?.totalPayment || 0;
  const pendingAmount = totalAmount - totalPaid;

  /* ================= ADD PAYMENT ================= */
  const addPayment = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/payment-history/add-payment", {
        bookingId,
        amountReceived: Number(form.amountReceived),
        paymentMethod: form.paymentMethod,
        paymentReceivedDate: form.paymentReceivedDate,
      });

      alert("✅ Payment Added");

      setForm({
        amountReceived: "",
        paymentMethod: "cash",
        paymentReceivedDate: "",
      });

      loadHistory();
      loadBooking();
    } catch (err) {
      alert(err.response?.data?.error || "❌ Payment Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div className="booking-container">
      <h2 className="page-title">Booking Payment History</h2>

      {/* ================= SUMMARY ================= */}
      <div className="summary-box">
        <div>
          <b>Total Amount:</b> ₹{totalAmount}
        </div>
        <div>
          <b>Total Paid:</b> ₹{totalPaid}
        </div>
        <div>
          <b>Pending Amount:</b>{" "}
          <span style={{ color: pendingAmount === 0 ? "green" : "red" }}>
            ₹{pendingAmount}
          </span>
        </div>
      </div>

      {/* ================= PAYMENT FORM ================= */}
      <form onSubmit={addPayment} className="booking-form">
        <input
          required
          type="number"
          placeholder="Amount Received"
          value={form.amountReceived}
          onChange={e =>
            setForm({ ...form, amountReceived: e.target.value })
          }
        />

        <select
          value={form.paymentMethod}
          onChange={e =>
            setForm({ ...form, paymentMethod: e.target.value })
          }
        >
          <option value="cash">Cash</option>
          <option value="upi">UPI</option>
          <option value="bank">Bank</option>
          <option value="cheque">Cheque</option>
          <option value="card">Card</option>
        </select>

        <input
          required
          type="date"
          value={form.paymentReceivedDate}
          onChange={e =>
            setForm({ ...form, paymentReceivedDate: e.target.value })
          }
        />

        <button disabled={loading || pendingAmount <= 0}>
          {loading ? "Saving..." : "Add Payment"}
        </button>
      </form>

      {/* ================= HISTORY TABLE ================= */}
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Method</th>
            <th>Amount</th>
            <th>Pending After Payment</th>
          </tr>
        </thead>

        <tbody>
          {history.length === 0 ? (
            <tr>
              <td colSpan="4">No payments yet</td>
            </tr>
          ) : (
            history.map(h => (
              <tr key={h._id}>
                <td>
                  {new Date(h.paymentReceivedDate).toLocaleDateString()}
                </td>
                <td>{h.paymentMethod.toUpperCase()}</td>
                <td>₹{h.amountReceived}</td>
                <td>₹{h.pendingAmount}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
