import { useEffect, useState } from "react";
import api from "../api";

export default function Bookings() {
  const [projects, setProjects] = useState([]);
  const [houses, setHouses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    projectId: "",
    houseNumber: "",
    customerName: "",
    mobileNo: "",
    paymentType: "cash",
    totalPayment: "",
    advancePayment: "",
  });

  /* ================= LOAD PROJECTS ================= */
  useEffect(() => {
    api.get("/lily").then((res) => {
      setProjects(res.data.data);
    });
    loadBookings();
  }, []);

  /* ================= LOAD BOOKINGS ================= */
  const loadBookings = async () => {
    try {
      const res = await api.get("/bookings");
      setBookings(res.data.data);
    } catch (err) {
      console.error("Booking load error", err);
    }
  };

  /* ================= PROJECT CHANGE ================= */
  const handleProjectChange = async (pid) => {
    setForm({
      ...form,
      projectId: pid,
      houseNumber: "",
      totalPayment: "",
    });

    try {
      const res = await api.get(`/lily/houses/${pid}`);
      setHouses(res.data.data.filter(h => h.status === "available"));
    } catch (err) {
      console.error(err);
      setHouses([]);
    }
  };

  /* ================= HOUSE CHANGE ================= */
  const handleHouseChange = (houseNo) => {
    const selectedHouse = houses.find(h => h.houseNumber === houseNo);

    setForm({
      ...form,
      houseNumber: houseNo,
      totalPayment: selectedHouse?.price || "",
    });
  };

  /* ================= SUBMIT ================= */
  const submitBooking = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post("/bookings/create", {
        ...form,
        projectId: Number(form.projectId),
        totalPayment: Number(form.totalPayment),
        advancePayment: Number(form.advancePayment),
      });

      alert("‚úÖ Booking Successful");

      setForm({
        projectId: "",
        houseNumber: "",
        customerName: "",
        mobileNo: "",
        paymentType: "cash",
        totalPayment: "",
        advancePayment: "",
      });

      setHouses([]);
      loadBookings(); // üîÑ refresh table
    } catch (err) {
      alert(err.response?.data?.message || "‚ùå Booking Failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 900, margin: "40px auto" }}>
      <h2>Create Booking</h2>

      {/* ================= FORM ================= */}
      <form onSubmit={submitBooking}>
        <select required value={form.projectId} onChange={(e) => handleProjectChange(e.target.value)}>
          <option value="">Select Project</option>
          {projects.map(p => (
            <option key={p.id} value={p.id}>{p.projectName}</option>
          ))}
        </select>

        <select required value={form.houseNumber} onChange={(e) => handleHouseChange(e.target.value)}>
          <option value="">Select House</option>
          {houses.map(h => (
            <option key={h.houseNumber} value={h.houseNumber}>{h.houseNumber}</option>
          ))}
        </select>

        <input required placeholder="Customer Name" value={form.customerName}
          onChange={(e) => setForm({ ...form, customerName: e.target.value })} />

        <input required placeholder="Mobile Number" value={form.mobileNo}
          onChange={(e) => setForm({ ...form, mobileNo: e.target.value })} />

        <select value={form.paymentType}
          onChange={(e) => setForm({ ...form, paymentType: e.target.value })}>
          <option value="cash">Cash</option>
          <option value="emi">EMI</option>
        </select>

        <input type="number" placeholder="Total Price" value={form.totalPayment} readOnly />

        <input required type="number" placeholder="Advance Payment" value={form.advancePayment}
          onChange={(e) => setForm({ ...form, advancePayment: e.target.value })} />

        <button type="submit" disabled={loading}>
          {loading ? "Booking..." : "Book House"}
        </button>
      </form>

      {/* ================= TABLE ================= */}
      <h2 style={{ marginTop: 50 }}>Booking List</h2>

      <table border="1" width="100%" cellPadding="8">
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>Project ID</th>
            <th>House</th>
            <th>Customer</th>
            <th>Mobile</th>
            <th>Payment</th>
            <th>Total</th>
            <th>Advance</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {bookings.length === 0 && (
            <tr>
              <td colSpan="9" align="center">No bookings found</td>
            </tr>
          )}

          {bookings.map((b) => (
            <tr key={b._id}>
              <td>{b.bookingId}</td>
              <td>{b.projectId}</td>
              <td>{b.houseNumber}</td>
              <td>{b.customerName}</td>
              <td>{b.mobileNo}</td>
              <td>{b.paymentType.toUpperCase()}</td>
              <td>{b.totalPayment}</td>
              <td>{b.advancePayment}</td>
              <td>{new Date(b.bookingDate).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
